<?php$lang['title'] = $lang['mysqlpassword'];if($_GET['action'] == 'show'){	$user = mysql_real_escape_string($_POST['user']);	$lang['body'] .= '		<form action="index.php?page=mysqlpassword&action=edit" method="post">			Usuario: <input type="text" name="user" value="'.$user.'" readonly="readonly"><br>			Contraseña Antigua: <input type="text" name="oldpassword"><br>			Nueva Contraseña: <input type="text" name="newpassword"><br>			Repetir Nueva Contraseña: <input type="text" name="repeatnewpassword"><br>			<input type="submit" name="submit" value="Editar">		</form>	';}elseif($_GET['action'] == 'edit'){	$user = mysql_real_escape_string($_POST['user']);	$oldpassword = mysql_real_escape_string($_POST['oldpassword']);	$newpassword = mysql_real_escape_string($_POST['newpassword']);	$repeatnewpassword = mysql_real_escape_string($_POST['repeatnewpassword']);	if(!empty($newpassword)){		if($newpassword == $repeatnewpassword){			$query = mysql_query('SELECT User FROM mysql.user WHERE User = "'.$user.'" AND Password = PASSWORD("'.$oldpassword.'")');			$exist = mysql_num_rows($query);			if($exist > 0){				$query = mysql_query('UPDATE mysql.user SET Password = PASSWORD("'.$newpassword.'") WHERE User = "'.$user.'" AND Password = PASSWORD("'.$oldpassword.'")');				$lang['body'] .= 'Contraseña Cambiada.';			}			else				$lang['body'] .= 'Este usuario no existe.';		}		else			$lang['body'] .= 'La nueva contraseña no coincide.';	}	else		$lang['body'] .= 'La nueva contraseña no debe estar vacia.';}else{	$query = mysql_query('SELECT User FROM mysql.user ORDER BY User ASC');	$lang['body'] = '<form action="index.php?page=mysqlpassword&action=show" method="post">Usuario: <select name="user">';	while($row = mysql_fetch_assoc($query)){		$lang['body'] .= '<option value="'.$row['User'].'">'.$row['User'].'</option>';	}	$lang['body'] .= '<input type="submit" name="submit" value="Editar"></select></form>';}?>