<?php$lang['title'] = $lang['refineproto'];if($_GET['action'] == 'show'){	$id = mysql_real_escape_string($_POST['id']);	$query = mysql_query('SELECT vnum0, count0, vnum1, count1, vnum2, count2, vnum3, count3, vnum4, count4, cost, src_vnum, result_vnum, prob FROM player.refine_proto WHERE id = "'.$id.'"');	while($row = mysql_fetch_assoc($query)){		$lang['body'] .= '		<form method="post" action="index.php?page=refineproto&action=edit">			ID: <input type="text" name="id" value="'.$id.'" readonly="readonly"><br>			vnum0: <input type="text" name="vnum0" value="'.$row['vnum0'].'"><br>			count0: <input type="text" name="count0" value="'.$row['count0'].'"><br>			vnum1: <input type="text" name="vnum1" value="'.$row['vnum1'].'"><br>			count1: <input type="text" name="count1" value="'.$row['count1'].'"><br>			vnum2: <input type="text" name="vnum2" value="'.$row['vnum2'].'"><br>			count2: <input type="text" name="count2" value="'.$row['count2'].'"><br>			vnum3: <input type="text" name="vnum3" value="'.$row['vnum3'].'"><br>			count3: <input type="text" name="count3" value="'.$row['count3'].'"><br>			vnum4: <input type="text" name="vnum4" value="'.$row['vnum4'].'"><br>			count4: <input type="text" name="count4" value="'.$row['count4'].'"><br>			cost: <input type="text" name="cost" value="'.$row['cost'].'"><br>			src_vnum: <input type="text" name="src_vnum" value="'.$row['src_vnum'].'"><br>			result_vnum: <input type="text" name="result_vnum" value="'.$row['result_vnum'].'"><br>			prob: <input type="text" name="prob" value="'.$row['prob'].'"><br>			<input type="submit" name="submit" value="Editar">		</form>		';	}}elseif($_GET['action'] == 'edit'){	$id = mysql_real_escape_string($_POST['id']);	$vnum0 = mysql_real_escape_string($_POST['vnum0']);	$count0 = mysql_real_escape_string($_POST['count0']);	$vnum1 = mysql_real_escape_string($_POST['vnum1']);	$count1 = mysql_real_escape_string($_POST['count1']);	$vnum2 = mysql_real_escape_string($_POST['vnum2']);	$count2 = mysql_real_escape_string($_POST['count2']);	$vnum3 = mysql_real_escape_string($_POST['vnum3']);	$count3 = mysql_real_escape_string($_POST['count3']);	$vnum4 = mysql_real_escape_string($_POST['vnum4']);	$count4 = mysql_real_escape_string($_POST['count4']);	$cost = mysql_real_escape_string($_POST['cost']);	$src_vnum = mysql_real_escape_string($_POST['src_vnum']);	$result_vnum = mysql_real_escape_string($_POST['result_vnum']);	$prob = mysql_real_escape_string($_POST['prob']);	$query = mysql_query('UPDATE player.refine_proto SET id = "'.$id.'", vnum0 = "'.$vnum0.'", count0 = "'.$count0.'", vnum1 = "'.$vnum1.'", count1 = "'.$count1.'", vnum2 = "'.$vnum2.'", count2 = "'.$count2.'", vnum3 = "'.$vnum3.'", count3 = "'.$count3.'", vnum4 = "'.$vnum4.'", count4 = "'.$count4.'", cost = "'.$cost.'", src_vnum = "'.$src_vnum.'", result_vnum = "'.$result_vnum.'", prob = "'.$prob.'" WHERE id = "'.$id.'"');	$lang['body'] .= 'Refine editado con exito.';}else{	$query = mysql_query('SELECT id FROM player.refine_proto ORDER BY id ASC');	$lang['body'] .= '<form action="index.php?page=refineproto&action=show" method="post">ID: <select name="id">';	while($row = mysql_fetch_assoc($query)){		$lang['body'] .= '<option value="'.$row['id'].'">'.$row['id'].'</option>';	}	$lang['body'] .= '<input type="submit" name="submit" value="Editar"></select></form>';}?>